mapper.select:
select * from lecture

    where lecname like concat('%',#{searchword},'%')

order by lectypea desc, lectypeb asc, lecname asc limit #{startnum},#{perpage}

mapper.update:
update lecture set teaname = #{teaname}, lecname = #{lecname}, lectypea = #{lectypea}, lectypeb = #{lectypeb}

    ,lecphoto = #{lecphoto}

where lecnum = #{lecnum}
